import{_ as e,o,c as a,e as p}from"./app.aa16d652.js";const i={},r=p('<h1 id="\u4E00\u6587\u8BFB\u61C2\u4EE5\u592A\u574A\u5408\u5E76" tabindex="-1"><a class="header-anchor" href="#\u4E00\u6587\u8BFB\u61C2\u4EE5\u592A\u574A\u5408\u5E76" aria-hidden="true">#</a> \u4E00\u6587\u8BFB\u61C2\u4EE5\u592A\u574A\u5408\u5E76</h1><p>\u57287\u670814\u65E5\u7684\u5F00\u53D1\u8005\u7535\u8BDD\u4F1A\u8BAE\u4E2D\uFF0C\u4EE5\u592A\u574A\u5F00\u53D1\u8005Tim Beiko\u786E\u8BA4\u4E86\u4EE5\u592A\u574A\uFF08Ethereum\uFF09\u5408\u5E76\u5C06\u4E8E 9 \u6708 19 \u65E5\u524D\u540E\u5B8C\u6210\uFF0C\u800C\u57288\u6708\u7B2C\u4E8C\u5468\u9884\u8BA1\u5C06\u8FDB\u884C\u7B2C\u4E09\u6B21\u4E5F\u662F\u6700\u540E\u4E00\u6B21\u6D4B\u8BD5\u7F51\u5408\u5E76Goerli\u3002</p><p>\u622A\u81F3\u76EE\u524D\uFF0C\u4EE5\u592A\u574A\u662F\u4E16\u754C\u4E0A\u751F\u6001\u6700\u4E3A\u5E9E\u5927\u3001\u590D\u6742\u7684\u533A\u5757\u94FE\u3002\u5408\u5E76\u662F\u4E00\u6B21\u524D\u6240\u672A\u6709\u4E14\u91CD\u8981\u7684\u4EE5\u592A\u574A\u5347\u7EA7\uFF0C\u5982\u679C\u987A\u5229\u8FDB\u884C\uFF0C\u4EE5\u592A\u574A\u5C06\u9996\u6B21\u5B8C\u6210\u5171\u8BC6\u673A\u5236\u4ECE PoW \u5411 PoS \u7684\u8F6C\u6362\uFF0C\u4E5F\u5C06\u7ED9\u5176\u4ED6\u533A\u5757\u94FE\u63D0\u4F9B\u4E00\u4E2A\u6781\u5177\u53C2\u8003\u6027\u7684\u8303\u4F8B\u3002\u4F5C\u4E3A\u6295\u8D44\u8005\uFF0C\u4E86\u89E3\u4EE5\u592A\u574A\u5408\u5E76\u53EF\u4EE5\u4E3A\u672A\u6765\u7684\u6295\u8D44\u51B3\u7B56\u505A\u597D\u94FA\u57AB\uFF0C\u8BA9\u6211\u4EEC\u7075\u6D3B\u5E94\u5BF9\u5408\u5E76\u5E26\u6765\u7684\u673A\u9047\u548C\u6311\u6218\u3002\u672C\u6587\u5C06\u8BE6\u7EC6\u4ECB\u7ECD\u4EE5\u592A\u574A\u5408\u5E76\u4E2D\u9700\u8981\u91CD\u70B9\u4E86\u89E3\u7684\u677F\u5757\uFF0C\u4EE5\u4F9B\u5404\u4F4D\u5FEB\u901F\u660E\u786E\u76F8\u5173\u6982\u5FF5\u3002</p><h1 id="\u4EC0\u4E48\u662F\u4EE5\u592A\u574A\u5408\u5E76" tabindex="-1"><a class="header-anchor" href="#\u4EC0\u4E48\u662F\u4EE5\u592A\u574A\u5408\u5E76" aria-hidden="true">#</a> \u4EC0\u4E48\u662F\u4EE5\u592A\u574A\u5408\u5E76</h1><p>\u201C\u5408\u5E76\u201D\u662F\u4EE5\u592A\u574A\u533A\u5757\u94FE\u4ECE\u4F7F\u7528\u5DE5\u4F5C\u91CF\u8BC1\u660E (PoW ) \u5171\u8BC6\u673A\u5236\u8F6C\u53D8\u4E3A\u6743\u76CA\u8BC1\u660E (PoS ) \u5171\u8BC6\u673A\u5236\u7684\u4E8B\u4EF6\u540D\u79F0\u3002\u5B83\u88AB\u79F0\u4E3A\u5408\u5E76\uFF0C\u56E0\u4E3A\u5B83\u662F\u76EE\u524D\u5E73\u884C\u8FD0\u884C\u7684\u4E24\u6761\u72EC\u7ACB\u533A\u5757\u94FE\u7684\u5408\u5E76\uFF1A\u4EE5\u592A\u574A\u4E3B\u7F51\u548C\u540D\u4E3A\u201C\u4FE1\u6807\u94FE\u201D\u7684\u7279\u6B8A\u7528\u9014\u533A\u5757\u94FE\u3002</p><p>\u4FE1\u6807\u94FE\u4E8E 2020 \u5E74 12 \u6708 1 \u65E5\u542F\u52A8\uFF0C\u5176\u552F\u4E00\u7528\u9014\u5C31\u662F\u6210\u4E3A\u4E00\u6761\u6743\u76CA\u8BC1\u660E (PoS) \u533A\u5757\u94FE\u3002\u4FE1\u6807\u94FE\u4E0A\u6CA1\u6709\u4EA4\u6613\u3001\u4EE3\u5E01\u6216Defi\u5E94\u7528\uFF0C\u662F\u4E00\u6761\u7A7A\u94FE\uFF0C\u8FD9\u6837\u7684\u7279\u6027\u8BA9\u5B83\u80FD\u591F\u548C\u4EE5\u592A\u574A\u533A\u5757\u94FE\u5408\u5E76\uFF0C\u5E76\u53D6\u4EE3\u539F\u6709\u7684PoW\u5171\u8BC6\u673A\u5236\u3002</p><h1 id="\u4EE5\u592A\u574A\u5408\u5E76\u662F\u600E\u6837\u5B9E\u73B0\u7684" tabindex="-1"><a class="header-anchor" href="#\u4EE5\u592A\u574A\u5408\u5E76\u662F\u600E\u6837\u5B9E\u73B0\u7684" aria-hidden="true">#</a> \u4EE5\u592A\u574A\u5408\u5E76\u662F\u600E\u6837\u5B9E\u73B0\u7684</h1><p>\u81EA\u4ECE\u542F\u52A8\u4E4B\u540E\uFF0C\u4FE1\u6807\u94FE\u4E00\u76F4\u4E0E\u4EE5\u592A\u574A\u4E3B\u7F51\u5E76\u884C\u8FD0\u884C\uFF0C\u76F8\u4E92\u72EC\u7ACB\u3002\u4FE1\u6807\u94FE\u662F\u57FA\u4E8EPoS\u673A\u5236\u7684\u533A\u5757\u94FE\uFF0C\u8D1F\u8D23\u51FA\u5757\u548C\u4EA4\u6613\u9A8C\u8BC1\u7684\u8282\u70B9\u4ECE\u8D28\u62BC ETH \u7684\u9A8C\u8BC1\u8005\u4E2D\u968F\u673A\u9009\u51FA\u3002\u6240\u4EE5\u4ECE\u4E0A\u7EBF\u7684\u7B2C\u4E00\u5929\u5F00\u59CB\uFF0C\u4FE1\u6807\u94FE\u5C31\u652F\u6301\u4E86 ETH \u8D28\u62BC\u3001\u5B58\u50A8\u529F\u80FD\uFF0C\u901A\u8FC7\u8D28\u62BC 32 \u4E2A\u6216\u66F4\u591A ETH\uFF0C\u4F60\u80FD\u6210\u4E3A\u9A8C\u8BC1\u8005\uFF0C\u83B7\u5F97\u8D28\u62BC\u6536\u76CA\u3002\u76EE\u524D\u88AB\u8D28\u62BC\u7684ETH\u4F1A\u5904\u4E8E\u4E00\u4E2A\u5C01\u5B58\u72B6\u6001\uFF0C\u672C\u91D1\u548C\u6536\u76CA\u7684\u63D0\u53D6\u8981\u7B49\u5230\u6B63\u5F0F\u5B8C\u6210\u5408\u5E76\u548C\u5347\u7EA7\u3002</p><p>\u73B0\u9636\u6BB5\u4FE1\u6807\u94FE\u7684\u529F\u80FD\u5F88\u5355\u7EAF\uFF0C\u5305\u62EC\u8D28\u62BC ETH\u3001\u968F\u673A\u9009\u62E9\u8282\u70B9\u51FA\u5757\u548C\u9A8C\u8BC1\u3001\u5BF9\u8282\u70B9\u8FDB\u884C\u5956\u52B1\u548C\u60E9\u7F5A\uFF0C\u8FD9\u4E9B\u529F\u80FD\u4EC5\u5141\u8BB8\u7EF4\u6301\u7F51\u7EDC\u6B63\u5E38\u8FD0\u884C\uFF0C\u50CF\u8D26\u6237\u6216\u8005\u667A\u80FD\u5408\u7EA6\u4E4B\u7C7B\u7684\u529F\u80FD\u662F\u4E0D\u652F\u6301\u7684\u3002\u76F8\u6BD4\u4E4B\u4E0B\uFF0C\u4EE5\u592A\u574A\u4E3B\u7F51\u5DF2\u7ECF\u662F\u4E00\u6761\u76F8\u5F53\u6210\u719F\u7684PoW\u94FE\uFF0C\u94FE\u4E0A\u5B58\u5728\u7740\u6570\u4EE5\u4E07\u8BA1\u7684\u5E94\u7528\u548C\u5927\u91CF\u8D44\u91D1\u3002\u7B49\u5230\u4FE1\u6807\u94FE\u5408\u5E76\u5230\u4EE5\u592A\u574A\u4E3B\u7F51\uFF0C\u4EE5\u592A\u574A\u7684 PoW \u5171\u8BC6\u5C42\u5C06\u88AB\u66FF\u6362\u6210PoS\u4FE1\u6807\u94FE\uFF0C\u4FE1\u6807\u94FE\u53EF\u80FD\u627F\u62C5\u4EA4\u6613\u6267\u884C\u3001\u6570\u636E\u53EF\u7528\u7684\u804C\u8D23\uFF0C\u4EA4\u6613\u72B6\u6001\u5219\u662F\u7EE7\u627F\u81EA\u539F\u4EE5\u592A\u574A\u4E3B\u7F51\u3002</p><p><img src="https://miro.medium.com/max/1400/0*qzydMy-djHyl_tDF" alt="img"></p><p>\u56FE\u7247\u6765\u81EA\u4EE5\u592A\u574A\u5B98\u7F51</p><p>\u4ECE\u5F53\u524D\u7684\u89C4\u5212\u6765\u770B\uFF0C\u4EE5\u592A\u574A\u60F3\u8981\u901A\u8FC7\u8F6C\u53D8\u5171\u8BC6\u673A\u5236\uFF08PoS\uFF09\u3001\u4F18\u5316\u6570\u636E\u5B58\u50A8\u548C\u9A8C\u8BC1\u6548\u7387\u6765\u63D0\u9AD8\u4E3B\u7F51\u6027\u80FD\uFF0CRolllup\u3001\u5206\u7247\u7B49\u6280\u672F\u4E5F\u4F1A\u5F97\u5230\u8FDB\u4E00\u6B65\u5E94\u7528\u3002\u4E3A\u4E86\u63A8\u52A8\u5171\u8BC6\u673A\u5236\u4ECE PoW \u5411 PoS \u8F6C\u53D8\uFF0C\u4EE5\u592A\u574A\u8FD8\u8BBE\u7F6E\u4E86\u201C\u96BE\u5EA6\u70B8\u5F39\u201D\u3002\u201C\u96BE\u5EA6\u70B8\u5F39\u201D\u4F1A\u4F7F PoW \u8BA1\u7B97\u96BE\u5EA6\u6307\u6570\u4E0A\u5347\uFF0C\u4ECE\u800C\u529D\u9000\u77FF\u5DE5\uFF0C\u786E\u4FDD\u4EE5\u592A\u574A\u5728\u5408\u5E76\u540E\u6210\u4E3A\u7EAF\u7CB9\u7684 PoS \u94FE\u3002</p><h1 id="\u4E3A\u4EC0\u4E48\u8981\u6709\u4EE5\u592A\u574A\u5408\u5E76" tabindex="-1"><a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u8981\u6709\u4EE5\u592A\u574A\u5408\u5E76" aria-hidden="true">#</a> \u4E3A\u4EC0\u4E48\u8981\u6709\u4EE5\u592A\u574A\u5408\u5E76</h1><ul><li>\u5B9E\u73B0\u5206\u7247\u6280\u672F\u5728\u4EE5\u592A\u574A\u7684\u5E94\u7528</li></ul><p>\u5206\u7247\u6280\u672F\u80FD\u5C06\u533A\u5757\u94FE\u7F51\u7EDC\u5212\u5206\u6210\u82E5\u5E72\u4E2A\u5B50\u7F51\u7EDC\uFF0C\u6BCF\u4E00\u4E2A\u5B50\u7F51\u7EDC\u90FD\u4F1A\u5305\u542B\u4E00\u90E8\u5206\u8282\u70B9\uFF0C\u7F51\u7EDC\u4E2D\u7684\u8D44\u6599\u5B58\u50A8\u548C\u4EA4\u6613\u90FD\u4F1A\u88AB\u968F\u673A\u5206\u914D\u5230\u5404\u4E2A\u5206\u7247\u4E2D\u5904\u7406\uFF0C\u8FD9\u6837\u4E00\u6765\uFF0C\u6BCF\u4E2A\u8282\u70B9\u53EA\u8981\u627F\u62C5\u4E00\u5C0F\u90E8\u5206\u5DE5\u4F5C\u91CF\uFF0C\u800C\u4E14\u4E0D\u540C\u5206\u7247\u4E0A\u7684\u4EA4\u6613\u53EF\u4EE5\u540C\u65F6\u8FDB\u884C\uFF0C\u6574\u4E2A\u7F51\u7EDC\u7684\u4EA4\u6613\u901F\u5EA6\u5C31\u66F4\u5FEB\u4E86\u3002\u5229\u7528\u4FE1\u6807\u94FE\u7684\u534F\u8C03\u4F5C\u7528\uFF0C\u4EE5\u592A\u574A\u7684\u7F51\u7EDC\u8D1F\u8F7D\u5C06\u5206\u5E03\u572864\u4E2A\u72EC\u7ACB\u7684\u5206\u7247\u4E2D\uFF0C\u8FD9\u4E9B\u5206\u7247\u540C\u65F6\u5904\u7406\u4FE1\u606F\uFF0C\u4ECE\u800C\u4F7F\u6574\u4F53\u4EA4\u6613\u65F6\u95F4\u66F4\u5FEB\u3001\u66F4\u9AD8\u6548\u3002</p><ul><li>\u5BF9\u73AF\u5883\u66F4\u53CB\u597D</li></ul><p>PoW \u5BF9\u673A\u5668\u3001\u7535\u529B\u7684\u8981\u6C42\u975E\u5E38\u9AD8\uFF0C\u4EE5\u6BD4\u7279\u5E01\u4E3A\u4F8B\uFF0C\u6BD4\u7279\u5E01\u7F51\u7EDC\u4E00\u5E74\u7684\u8017\u7535\u91CF\u5927\u7EA6\u662F 1213.6 \u4EBF\u5EA6\uFF0C\u8D85\u8FC7\u963F\u6839\u5EF7\u3001\u8377\u5170\u3001\u963F\u62C9\u4F2F\u8054\u5408\u914B\u957F\u56FD\u5168\u5E74\u8017\u7535\u91CF\u3002\u5728\u5408\u5E76\u4E4B\u540E\uFF0C\u4F7F\u7528PoS\u7684\u4EE5\u592A\u574A\u7F51\u7EDC\u5C06\u51CF\u5C11 99.95% \u4EE5\u4E0A\u7684\u8017\u7535\u91CF\u3002</p><ul><li>\u66F4\u9AD8\u7684\u5B89\u5168\u6027\u548C\u53BB\u4E2D\u5FC3\u5316\u7A0B\u5EA6</li></ul><p>\u76F8\u6BD4PoW\u590D\u6742\u7684\u673A\u5668\u914D\u7F6E\u3001\u8FD0\u7EF4\uFF0CPoS \u8282\u70B9\u7684\u53C2\u4E0E\u95E8\u69DB\u66F4\u4F4E\uFF0C\u7279\u522B\u662F\u5728\u8D8A\u6765\u8D8A\u591A\u670D\u52A1\u5546\u652F\u6301\u5C0F\u989DETH\u8D28\u62BC\u540E\uFF0C\u4E0D\u8BA1\u5212\u6295\u5165\u9AD8\u989D\u8D44\u91D1\u7684\u666E\u901A\u4EBA\u4E5F\u80FD\u6210\u4E3A\u8282\u70B9\u7684\u4E00\u90E8\u5206\u3002\u5B89\u5168\u65B9\u9762\uFF0CVitalik\u66FE\u53D1\u6587\u8BBA\u8BC1PoS \u7F51\u7EDC\u7684\u653B\u51FB\u6210\u672C\u9AD8\u4E8E PoW \u7F51\u7EDC\uFF0C\u800C\u4E14\u5728\u88AB\u653B\u51FB\u540E\uFF0CPoS \u7684\u6062\u590D\u80FD\u529B\u4E5F\u4F18\u4E8E PoW \u7F51\u7EDC\u3002\u4F46\u73B0\u6709\u7684\u8FD9\u4E9B\u89C2\u70B9\u8FD8\u9700\u8981\u672A\u6765\u5B9E\u8DF5\u8FDB\u4E00\u6B65\u9A8C\u8BC1\u3002</p><h1 id="\u5408\u5E76\u5BF9eth\u6295\u8D44\u8005\u6709\u4EC0\u4E48\u5F71\u54CD" tabindex="-1"><a class="header-anchor" href="#\u5408\u5E76\u5BF9eth\u6295\u8D44\u8005\u6709\u4EC0\u4E48\u5F71\u54CD" aria-hidden="true">#</a> \u5408\u5E76\u5BF9ETH\u6295\u8D44\u8005\u6709\u4EC0\u4E48\u5F71\u54CD</h1><p>\u5408\u5E76\u5BF9 ETH \u7ECF\u6D4E\u7684\u6539\u53D8\u4E3B\u8981\u6709\u4E24\u65B9\u9762\uFF1A\u4E00\u662F\u51CF\u5C11 ETH \u7684\u53D1\u884C\u91CF\uFF0C\u4E8C\u662F ETH \u4F1A\u9010\u6E10\u8F6C\u53D8\u4E3A\u4E00\u79CD\u539F\u751F\u7684\u6536\u76CA\u8D44\u4EA7\u3002</p><p>PoS \u80FD\u4EE5\u4F4E\u5EC9\u7684\u6210\u672C\u63D0\u4F9B\u6700\u9AD8\u7EA7\u522B\u7684\u533A\u5757\u94FE\u5B89\u5168\uFF0C\u8FD9\u4E2A\u8FC7\u7A0B\u4E2D\u4F1A\u51CF\u5C11\u4E3A\u7EF4\u62A4\u5B89\u5168\u800C\u53D1\u884C\u7684 ETH \u6570\u91CF\uFF08\u5373\u652F\u4ED8\u7ED9\u77FF\u5DE5\u7684\u6210\u672C\uFF09\uFF0C\u4F7F\u4EE5\u592A\u574A\u5C06\u6BCF\u5E74\u7684 ETH \u589E\u53D1\u6BD4\u4F8B\u4ECE 4.3% \u51CF\u5C11\u5230 0.43%\u3002\u56E0\u4E3A PoW \u77FF\u5DE5\u4E60\u60EF\u7ACB\u5373\u5356\u6389\u6536\u5230\u7684\u5927\u90E8\u5206\u5956\u52B1\uFF0C\u800C\u4E14\u968F\u7740\u65F6\u95F4\u7684\u63A8\u79FB\uFF0C\u88AB\u5356\u6389\u7684\u90E8\u5206\u5360\u603B\u5F00\u91C7\u91CF\u7684\u6BD4\u4F8B\u51E0\u4E4E\u662F90%\u4EE5\u4E0A\uFF0CPoS\u53EF\u4EE5\u6539\u53D8\u8FD9\u4E00\u73B0\u72B6\uFF0C\u51CF\u5C1190%\u4EE5\u4E0A\u7684\u65B0ETH\u53D1\u884C\u3002</p><p>\u65B0ETH\u53D1\u884C\u91CF\u7684\u51CF\u5C11\u4E00\u822C\u610F\u5473\u7740\u5E01\u4EF7\u4E0A\u5347\uFF0C\u800C\u4E14\u73B0\u5728\u652F\u4ED8\u7ED9\u77FF\u5DE5\u7684\u8D39\u7528\u5C06\u8F6C\u79FB\u7ED9\u8D28\u62BC\u8005/\u9A8C\u8BC1\u8005\uFF0C\u8BA9\u8D28\u62BC\uFF08staking\uFF09\u6536\u76CA\u7387\u53D8\u9AD8\u3002IntoTheBlock\u8BA1\u7B97\u51FA\uFF0C\u52A0\u4E0A\u8D39\u7528\u5956\u52B1\uFF0C\u76EE\u524D3.8%\u7684\u5E74\u56DE\u62A5\u7387\u5C06\u5347\u81F37.4%\u3002\u7EFC\u5408\u8FD9\u4E9B\u56E0\u7D20\uFF0CETH\u7684\u9700\u6C42\u4F1A\u8FDB\u4E00\u6B65\u589E\u52A0\uFF0C\u6210\u4E3A\u7A33\u5B9A\u7684\u6536\u76CA\u8D44\u4EA7\u3002</p><p>\u5F53\u7136\uFF0C\u5982\u679C\u4F60\u5DF2\u7ECF\u6301\u6709\u4E00\u5B9AETH\uFF0C\u4E5F\u53EF\u4EE5\u73B0\u5728\u5C31\u53C2\u4E0EETH\u8D28\u62BC\u3002\u6211\u4EEC\u63A8\u8350\u901A\u8FC7\u53EF\u4E50\u77FF\u6C60\u8FDB\u884C\u5FEB\u6377\u3001\u5B89\u5168\u7684ETH \u8D28\u62BC\u3002</p><p><img src="https://miro.medium.com/max/1400/0*04U4IuqPG8Yom9YP" alt="img"></p><p>\u5B89\u5168\u6027\u8BF4\u660E\uFF1A\u53EF\u4E50\u77FF\u6C60\u7684staking\u667A\u80FD\u5408\u7EA6\u5DF2\u901A\u8FC7\u533A\u5757\u94FE\u6743\u5A01\u673A\u6784\u6162\u96FE\u79D1\u6280\u7684\u5BA1\u8BA1\uFF0C\u5176\u5B89\u5168\u6027\u548C\u667A\u80FD\u5408\u7EA6\u4EE3\u7801\u5747\u53EF\u67E5\u3002\u4E14\u53EF\u4E50\u77FF\u6C60\u652F\u6301\u4F7F\u7528Ledger\u786C\u4EF6\u94B1\u5305\u3002</p><p>\u7075\u6D3B\u8D28\u62BC\uFF1A\u4E0D\u540C\u4E8E\u5927\u90E8\u5206\u53EA\u6709\u5355\u4E00\u670D\u52A1\u7684\u77FF\u6C60\uFF0C\u53EF\u4E50\u77FF\u6C60\u652F\u6301\u4E24\u79CD\u670D\u52A1\u6A21\u5F0F\uFF0C\u4F60\u53EF\u4EE5\u81EA\u7531\u9009\u62E9\u7B26\u5408\u81EA\u5DF1\u9700\u8981\u7684\u65B9\u5F0F\u3002\u5BF9\u6BD4\u5176\u4ED6\u77FF\u6C60\uFF0C\u5B83\u6536\u53D6\u7684\u670D\u52A1\u8D39\u4E5F\u8F83\u4E3A\u5408\u7406\u3002</p><p>\u76EE\u524D\u53EF\u4E50\u77FF\u6C60\u652F\u6301\u7684\u5E01\u79CD\u4E3AETH\uFF0C\u8FD1\u671F\u8FD8\u4F1A\u6709\u66F4\u591A\u8D28\u62BC\u7F51\u7EDC\u63A8\u51FA\uFF0C\u5177\u4F53\u53EF\u4EE5\u53BB\u5B98\u7F51\u67E5\u770B\u3002</p><p><strong>\u53EF\u4E50\u77FF\u6C60\u5B98\u7F51\uFF1Ahttps://www.kelepool.com/</strong></p>',29),t=[r];function h(d,l){return o(),a("div",null,t)}var n=e(i,[["render",h],["__file","merge.html.vue"]]);export{n as default};
